package com.training.demos;

public class IfDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=30,y=20,z=40;
		if (x>y && x>z) {
			System.out.println("X is greater");
		}
		else if (y>x && y>z){
			System.out.println("Y is greater");
		}
		else {
			System.out.println("Z is greater");
		}

	}

}
